import styled from 'styled-components';
import { NavLink } from "react-router-dom";
import {AiOutlineHome, AiOutlineComment, AiOutlineBell, AiOutlineUser} from "react-icons/ai";

export default function Navbar() {
  return (
    <>
      <Container>
        <NavLink className={({ isActive }) => (isActive ? "active" : "not")} to="/">
          <AiOutlineHome/>
        </NavLink>
        <NavLink className={({ isActive }) => (isActive ? "active" : "not")} to="/chattinglist">
          <AiOutlineComment/>
        </NavLink>
        <NavLink className={({ isActive }) => (isActive ? "active" : "not")} to="/notification">
          <AiOutlineBell/>
        </NavLink>
        <NavLink className={({ isActive }) => (isActive ? "active" : "not")} to="/mypage">
          <AiOutlineUser/>
        </NavLink>
      </Container>
    </>
  );
}

const Container = styled.div`
  width: 100%;
  height: 60px;
  box-shadow: 0 -5px 10px -8px lightgrey;
  position: sticky;
  top: 100%;
  display: flex;
  align-items: center;
  justify-content: space-around;

  svg {
    font-size: 30px;
  }

  a {
    color: ${({ theme }) => theme.colors.main_blue};

		&:hover {
      color: ${({ theme }) => theme.colors.darker_blue};
		}
		&.active {
      color: ${({ theme }) => theme.colors.dark_blue};
		}

  // 태블릿 : 1200px ~ 768px :: 768px 이상 적용되는 css
  @media only screen and (min-width: 768px) {
    width: 100%;
    height: 60px;
    box-shadow: 0 -5px 10px -8px lightgrey;
    position: sticky;
    top: 100%;
    display: flex;
    align-items: center;
    justify-content: space-around;

    svg {
      font-size: 30px;
    }

    a {
      color: ${({ theme }) => theme.colors.main_blue};

      &:hover {
        color: ${({ theme }) => theme.colors.darker_blue};
      }
      &.active {
        color: ${({ theme }) => theme.colors.dark_blue};
      }
  }
  // PC : 1200px 이상 :: 1200px 이상 적용되는 css
  @media only screen and (min-width: 1200px) {
    width: 100%;
    height: 60px;
    box-shadow: 0 -5px 10px -8px lightgrey;
    position: sticky;
    top: 100%;
    display: flex;
    align-items: center;
    justify-content: space-around;

    svg {
      font-size: 30px;
    }

  }
`;
